
const slider = document.querySelector(".header-slider")
const header = document.querySelector('.header')
slider.addEventListener("click", (e) => {
    const current = e.target
    if (current.dataset.number) {
        const activeEl = document.querySelector('.active')
        if (current !== activeEl) {
            activeEl.classList.remove('active');
            current.classList.add('active');

            header.style.backgroundImage=`url(dist/img/banner${current.dataset.number}.png)`
        }


    }
})
const menu = document.querySelector(".menu");
menu.addEventListener("click", (e) => {
  let searchList = null;
  if (e.target.closest("#tour")) {
    searchList = ".benefits-list--small";
  } else if (e.target.closest("#benefits")) {
    searchList = ".benefits-list--big";
  }
  if (searchList === null) return;
  const currentUl = document.querySelector(searchList);

  currentUl.classList.contains("hide-header-block")
    ? currentUl.classList.remove("hide-header-block")
    : currentUl.classList.add("hide-header-block");
});

let swiper = new Swiper(".slider", {
    slidesPerView: 3,
    centeredSlides: true,
    spaceBetween: 30,
    pagination: {
        el: ".swiper-pagination",
        clickable: true,
    },
    navigation: {
        nextEl: ".swiper-button-next",
        prevEl: ".swiper-button-prev",
    },
    breakpoints: {
        // when window width is >= 320px
        320: {
            slidesPerView: 1,
            spaceBetween: 10
        },
        // when window width is >= 480px
        500: {
            slidesPerView: 2,
            spaceBetween: 20
        },
        // when window width is >= 640px
        750: {
            slidesPerView: 3,
            spaceBetween: 30
        }
    }
});